
public class ControleFlow {
	/*
	 * if statement
	 * 
	 * 
	 * if (conditon){
	 * statement ()}
	 * nested if 
	 */

	public static void main(String[] args) {
		int num = 70;
		if(num < 1000) {
			System.out.println("less than 100");
			
			
		}
		if(num < 10) {
			System.out.println("less than 100");
			if(num > 50) {
				System.out.println("greater  than 50");
			}
			
		}
		// TODO Auto-generated method stub
		
		
		

	}

}

