
public class LogicalOpretors {
	/*
	 * three types
	 * &&,//!
	 * && this will excute both condition (and)
	 * || will only execute the condtion that is true (or)
	 * ! will cheack the value is null or not (not ==)
	 * 
	 *
	 
	 */

	public static void main(String[] args) {
		
		boolean b1 = true;
		boolean b2 = false;
		System.out.println("&&"+(b1&&b2));
		System.out.println("||"+(b1||b2));
		System.out.println("!"+(b1&&b2));
		
		// TODO Auto-generated method stub

	}

}
